    [InlineData("1.0", VersionOperator.GreaterThan, ">1.0")]
    [InlineData("1.0", VersionOperator.GreaterOrEqual, ">=1.0")]
    [InlineData("1.0", VersionOperator.LessOrEqual, "<=1.0")]
    [InlineData("1.0", VersionOperator.LessThan, "<1.0")]
