using System;
using System.IO;
using System.Linq;
using JetBrains.Annotations;
using Mono.Cecil;
using Mono.Cecil.Cil;
using RailManagerInstaller.Abstractions;

namespace RailManagerInstaller;

[PublicAPI]
public static class Patcher
{
    private const string ModManagerInterfaces = "Railroader.ModManager.Interfaces";
    private const string ModManager = "Railroader.ModManager";
    private const string ModManagerType = "Railroader.ModManager.ModManager";
    private const string AssemblyCsharpDll = "Assembly-CSharp.dll";

    public static Action PatchGame = PatchGameCore;
    
    public static void PatchGameCore() {

        var path = Path.Combine(AppServices.Directory.GetCurrentDirectory(), "Railroader_Data", "Managed");
        var modInterfaces = GetFile(path, ModManagerInterfaces + ".dll");
        var modInjector = GetFile(path, ModManager + ".dll");
        var assemblyCsharp = GetFile(path, AssemblyCsharpDll);

        var resolver = new DefaultAssemblyResolver();
        resolver.RemoveSearchDirectory(".");
        resolver.RemoveSearchDirectory("bin");
        resolver.AddSearchDirectory(path);

        var readerParameters = new ReaderParameters {
            InMemory = true,
            AssemblyResolver = resolver
        };

        
        
        var assemblyCsharpModule = AppServices.ModuleDefinition.ReadModule(assemblyCsharp, readerParameters);
        if (assemblyCsharpModule.AssemblyReferences.Any(o => o.Name == ModManager)) {
            AppServices.Console.WriteLine("Railroader is already patched.", ConsoleColor.DarkYellow);
            return;
        }

        InjectModule(assemblyCsharpModule, ModManagerInterfaces, modInterfaces, readerParameters);
        var modManager = InjectModule(assemblyCsharpModule, ModManager, modInjector, readerParameters);

        var hasPatch = PatchLogManager(assemblyCsharpModule, modManager);
        if (!hasPatch) {
            return;
        }

        AppServices.File.Copy(assemblyCsharp, assemblyCsharp.Replace(".dll", "_original.dll"));
        assemblyCsharpModule.Write(assemblyCsharp);
        AppServices.File.SetLastWriteTime(assemblyCsharp.Replace(".dll", "_original.dll"), AppServices.File.GetLastWriteTime(assemblyCsharp));
        AppServices.Console.WriteLine("Successfully patched game.");
    }

    private static bool PatchLogManager(IModuleDefinition assemblyCsharp, IModuleDefinition modManager) {
        // Use Mono.Cecil to create static constructor on Logging.LogManager type that calls Railroader.ModManager.ModManager.Bootstrap()

        var logManager = assemblyCsharp.GetType("Logging.LogManager");
        if (logManager == null) {
            throw new InstallerException("Could not find Logging.LogManager type.");
        }

        var modManagerType = modManager.GetType(ModManagerType);
        if (modManagerType == null) {
            throw new InstallerException($"Could not find {ModManagerType} type.");
        }

        var bootstrapMethod = modManagerType.Methods.FirstOrDefault(m => m.Name == "Bootstrap");
        if (bootstrapMethod == null) {
            throw new InstallerException($"Could not find {ModManagerType}.Bootstrap method.");
        }

        // Import Bootstrap method
        var bootstrapMethodRef = assemblyCsharp.ImportReference(bootstrapMethod);

        var cctor = logManager.Methods.FirstOrDefault(m => m.Name == ".cctor");
        if (cctor != null) {
            // Check if Bootstrap is already called
            if (cctor.Body.Instructions.Any(o => o.OpCode == OpCodes.Call && o.Operand == bootstrapMethodRef)) {
                AppServices.Console.WriteLine("Logging.LogManager static constructor already calls ModManager.Bootstrap. Skipping patch.",
                    ConsoleColor.DarkYellow);
                return false;
            }
        } else {
            // Create new empty cctor
            const MethodAttributes cctorAttributes = MethodAttributes.Static | MethodAttributes.Private | MethodAttributes.HideBySig |
                                                     MethodAttributes.SpecialName | MethodAttributes.RTSpecialName;
            cctor = new(".cctor", cctorAttributes, assemblyCsharp.TypeSystem.Void);
            logManager.Methods.Add(cctor);

            var il = cctor.Body.GetILProcessor();
            il.Emit(OpCodes.Ret);
        }

        // Append Bootstrap call to the end
        var ilProcessor = cctor.Body.GetILProcessor();
        var firstInstruction = cctor.Body.Instructions[0]!;
        var callBootstrap = ilProcessor.Create(OpCodes.Call, bootstrapMethodRef);

        // Insert Bootstrap call before first instruction
        ilProcessor.InsertBefore(firstInstruction, callBootstrap);
        return true;
    }

    private static IModuleDefinition InjectModule(IModuleDefinition assemblyCsharpModule, string name, string path, ReaderParameters readerParameters) {
        var modInterfacesModule = AppServices.ModuleDefinition.ReadModule(path, readerParameters);
        var modInterfacesReference = new AssemblyNameReference(name, modInterfacesModule.Assembly.Name.Version);
        assemblyCsharpModule.AssemblyReferences.Add(modInterfacesReference);
        return modInterfacesModule;
    }

    private static string GetFile(string path, string name) {
        var filePath = Path.Combine(path, name);
        if (!AppServices.File.Exists(filePath)) {
            throw new InstallerException($"Could not locate file '{name}'.");
        }

        return filePath;
    }
}

using System;
using System.Diagnostics.CodeAnalysis;
using System.IO;
using System.Reflection;
using JetBrains.Annotations;
using RailManagerInstaller.Abstractions;

namespace RailManagerInstaller;

[PublicAPI]
public static class Program
{
    private const string Railroader = "Railroader.exe";
    
    public static void Main() {
        try {
            RunInstaller();
        } catch (InstallerException ex) {
            AppServices.Console.WriteLine(ex.Message!, ConsoleColor.Red);
            if (ex.InnerException != null) {
                AppServices.Console.WriteLine(" - " + ex.InnerException.Message!, ConsoleColor.Red);
            }
        } catch (GamePathException ex) {
            AppServices.Console.WriteLine(ex.Message!, ConsoleColor.Red);
            AppServices.Console.WriteLine("Could not determine Railroader directory automatically.", ConsoleColor.Red);
            AppServices.Console.WriteLine("Move this installer into your game's directory, then run again.");
        } catch (Exception ex) {
            AppServices.Console.WriteLine("Unexpected error:", ConsoleColor.Red);
            AppServices.Console.WriteLine(ex.ToString(), ConsoleColor.Red);
        } finally {
            AppServices.Console.WriteLine("Press any key to exit.", ConsoleColor.White);
            AppServices.Console.ReadKey();
        }
    }

    private static IAssembly _ExecutingAssembly = null!;
    
    internal static void RunInstaller() {
        try {
            _ExecutingAssembly = AppServices.Assembly.GetExecutingAssembly();
            var assemblyName = _ExecutingAssembly.GetName().Name;
            AppServices.Console.Write(assemblyName + " ");
            AppServices.Console.WriteLine(_ExecutingAssembly.GetName().Version, ConsoleColor.DarkGreen);
            AppServices.Console.SetTitle($"{assemblyName} {_ExecutingAssembly.GetName().Version}");
        } catch (PlatformNotSupportedException) {
        }

        // stryker disable once assignment
        AppDomain.CurrentDomain.AssemblyResolve += ResolveInternalAssembliesHandler;

        ResolveGameDirectory();
        ResourceExtractor.ExtractFiles(_ExecutingAssembly);
        Patcher.PatchGame();
        AppServices.Directory.CreateDirectory("Mods");

        AppServices.Console.WriteLine("Installation complete!", ConsoleColor.DarkGreen);
    }

    private static void ResolveGameDirectory() {
        var path = GameDirectoryResolver.TryResolveGameDirectory(_ExecutingAssembly);
        if (path == null) {
            throw new GamePathException($"Could not find {Railroader} using Steam's Library.");
        }

        if (AppServices.File.Exists(Path.Combine(path, Railroader))) {
            AppServices.Console.WriteLine("Found Railroader using Steam's Library.");
            AppServices.Directory.SetCurrentDirectory(path);
            return;
        }

        throw new GamePathException($"Could not find {Railroader} (Steam's Library path is invalid).");
    }

    [ExcludeFromCodeCoverage]
    private static Assembly ResolveInternalAssembliesHandler(object sender, ResolveEventArgs args) => ResolveInternalAssemblies(sender, args).Assembly;

    public static IAssembly ResolveInternalAssemblies(object sender, ResolveEventArgs args) {
        var name = new AssemblyName(args.Name!).Name;
        if (!name.StartsWith("Mono.Cecil") && !name.StartsWith("Newtonsoft.Json")) {
            throw new InstallerException($"Could not load missing assembly: {name}");
        }

        var executingAssembly = AppServices.Assembly.GetExecutingAssembly();
        var stream = executingAssembly.GetManifestResourceStream($"Assemblies/{name}.dll")
                     ?? throw new InstallerException($"Embedded assembly not found: {name}.dll");

        var buffer = new byte[stream.Length];
        _ = stream.Read(buffer, 0, buffer.Length);
        return AppServices.Assembly.Load(buffer);
    }
}

using System;
using System.IO;
using JetBrains.Annotations;
using RailManagerInstaller.Abstractions;

namespace RailManagerInstaller;

[PublicAPI]
public static class ResourceExtractor
{
    public static Action<IAssembly> ExtractFiles = ExtractFilesCore;
    
    public static void ExtractFilesCore(IAssembly executingAssembly) {
        var prefix = typeof(Program).FullName!.Replace(nameof(Program), "Assemblies");

        string[] assemblies = [
            "0Harmony.dll",
            "Mono.Cecil.dll",
            "Mono.CSharp.dll",
            "RailManager.dll",
            "RailManager.Interfaces.dll"
        ];

        AppServices.Console.WriteLine("Extracting files ...");
        foreach (var assembly in assemblies) {
            var path = Path.Combine(AppServices.Directory.GetCurrentDirectory(), "Railroader_Data", "Managed", assembly);

            AppServices.Console.WriteLine(path, ConsoleColor.DarkCyan);

            using var stream = executingAssembly.GetManifestResourceStream($"{prefix}.{assembly}")!;

            using var fileStream = AppServices.File.Open(path, FileMode.OpenOrCreate, FileAccess.Write);
            stream.CopyTo(fileStream);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Text.RegularExpressions;
using JetBrains.Annotations;

namespace RailManagerInstaller;

[ExcludeFromCodeCoverage]
public class VdfException(string message) : Exception(message);

public delegate VdfEntry VdfEntryLoader(string fullPath);

[PublicAPI]
public class VdfEntry : Dictionary<string, object>
{
    public static VdfEntryLoader Load = LoadEntry;

    public static VdfEntry LoadEntry(string fullPath) {
        var lines = AppServices.File.ReadAllLines(fullPath);
        return Parse(lines);
    }

    public T? FindValue<T>(string name) {
        TryGetValue(name, out var value);
        return value is T o ? o : default;
    }

    public static VdfEntry Parse(string[] lines) {
        var i = 0;
        return Parse(lines, 0, ref i);
    }

    private static VdfEntry Parse(string[] lines, int level, ref int i) {
        var entry = new VdfEntry();
        for (; i < lines.Length; i++) {
            var line = lines[i].Trim();
            if (line == "}") {
                break;
            }

            if (IsSimpleValue(line, out var key, out var value)) {
                entry.Add(key, value);
                continue;
            }

            if (IsKey(line, out key)) {
                i++;
                if (lines[i].Trim() != "{") {
                    throw new VdfException("Expected '{' after key");
                }

                i++;
                var nested = Parse(lines, level + 1, ref i);
                entry.Add(key, nested);
                continue;
            }

            throw new VdfException("Unexpected line in vdf file");
        }

        if (i == lines.Length && level > 0) {
            throw new VdfException("Unexpected end of vdf file");
        }

        return entry;
    }

    private static readonly Regex _SimpleValue = new(@"^\s*""(?<key>[^""]+)""\s+""(?<value>[^""]+)""\s*$", RegexOptions.Compiled);

    private static bool IsSimpleValue(string line, [NotNullWhen(true)] out string? key, [NotNullWhen(true)] out string? value) {
        key = null;
        value = null;

        var match = _SimpleValue.Match(line);
        if (match.Success) {
            key = match.Groups["key"]!.Value;
            value = match.Groups["value"]!.Value;
        }

        return match.Success;
    }

    private static readonly Regex _Key = new(@"^\s*""(?<key>[^""]+)""\s*$", RegexOptions.Compiled);

    private static bool IsKey(string line, [NotNullWhen(true)] out string? key) {
        key = null;

        var match = _Key.Match(line);
        if (match.Success) {
            key = match.Groups["key"]!.Value;
        }

        return match.Success;
    }
}

