namespace RailManager.Features;

/// <summary>
///     Action that applies post-compilation patches to a mod assembly using Mono.Cecil.
/// </summary>
/// <param name="definition">The mod definition to patch.</param>
/// <param name="pluginPatchers">
///     Optional patchers. If <c>null</c>, <see cref="CodePatcher.DefaultPluginPatchers" /> are used.
/// </param>
/// <returns><c>true</c> if patching succeeded or was not needed; <c>false</c> on error.</returns>
public delegate bool PatchModAction(ModDefinition definition, TypePatcherInfo[]? pluginPatchers = null);

/// <summary>
///     Applies runtime patches to a compiled mod assembly (UI buttons, Harmony hooks, etc.).
///     All I/O and assembly operations are injected via delegates for full unit-test isolation.
/// </summary>
public static class CodePatcher
{
    /// <summary>
    ///     Default built-in patchers for common plugin interfaces.
    /// </summary>
    public static readonly TypePatcherInfo[] DefaultPluginPatchers = [
        new(typeof(ITopRightButtonPlugin), TopRightButtonPluginPatcher.Factory),
        new(typeof(IHarmonyPlugin), HarmonyPluginPatcher.Factory)
    ];

    /// <summary>
    ///     Creates a <see cref="PatchModAction" /> pre-configured with production dependencies.
    /// </summary>
    /// <returns>A fully-wired delegate ready for use.</returns>
    [ExcludeFromCodeCoverage]
    public static PatchModAction Create() =>
        (definition, pluginPatchers) => ApplyPatches(
            Log.Logger.ForSourceContext(), AssemblyDefinitionStatic.Instance,
            FileSystem.Instance,
            definition, pluginPatchers ?? DefaultPluginPatchers
        );

    /// <summary>
    ///     Executes all registered <paramref name="pluginPatchers" /> against the compiled mod DLL.
    /// </summary>
    /// <param name="logger">Logger for diagnostic output.</param>
    /// <param name="assemblyDefinitionStatic"></param>
    /// <param name="fileSystem"></param>
    /// <param name="definition">Mod definition containing path and identifier.</param>
    /// <param name="pluginPatchers">Array of patchers to apply.</param>
    /// <returns><c>true</c> if patching succeeded or no patches were needed; <c>false</c> on any error.</returns>
    public static bool ApplyPatches(
        ILogger logger, IAssemblyDefinitionStatic assemblyDefinitionStatic,
        IFileSystem fileSystem, 
        ModDefinition definition, TypePatcherInfo[] pluginPatchers
    ) {
        if (pluginPatchers.Length == 0) {
            return true;
        }

        logger.Information("Patching mod {ModId} ...", definition.Identifier);

        bool                success;
        var                 assemblyPath          = Path.Combine(definition.BasePath, definition.Identifier + ".dll");
        var                 tempFilePath          = Path.ChangeExtension(assemblyPath, ".patched.dll");
        var                 shouldReplaceOriginal = false;
        IAssemblyDefinition? assemblyDefinition    = null;
        try {
            var resolver = CreateAssemblyResolver(fileSystem.Directory, definition, assemblyPath);

            var readParameters = new ReaderParameters { AssemblyResolver = resolver };
            assemblyDefinition = assemblyDefinitionStatic.ReadAssembly(assemblyPath, readParameters);
            if (assemblyDefinition == null) {
                logger.Error("Failed to load definition for assembly {AssemblyPath} for mod {ModId}", assemblyPath, definition.Identifier);
                success = false;
            } else {
                (shouldReplaceOriginal, success) = TryPatchAssemblyDefinition(logger, definition, pluginPatchers, assemblyDefinition);

                if (shouldReplaceOriginal) {
                    assemblyDefinition.Write(tempFilePath);
                    logger.Debug("Wrote patched assembly to temporary file {TempPath} for mod {ModId}", tempFilePath, definition.Identifier);
                } else {
                    logger.Information("No patches were applied to assembly {AssemblyPath} for mod {ModId}", assemblyPath, definition.Identifier);
                }
            }
        } finally {
            assemblyDefinition?.Dispose();
        }

        if (shouldReplaceOriginal) {
            try {
                fileSystem.File.Delete(assemblyPath);
                fileSystem.File.Move(tempFilePath, assemblyPath);
            } catch (Exception exc) {
                logger.Error(exc, "Failed to replace original assembly for mod {ModId}", definition.Identifier);
                success = false;
            }
        }

        if (!success) {
            logger.Error("Failed to apply patches to assembly {AssemblyPath} for mod {ModId}", assemblyPath, definition.Identifier);
        } else {
            logger.Information("Patching complete for mod {ModId}", definition.Identifier);
        }

        return success;
    }

    /// <summary>
    ///     Builds a <see cref="DefaultAssemblyResolver" /> that includes:
    ///     <list type="bullet">
    ///         <item>Game managed assemblies (<c>Railroader_Data\Managed</c>)</item>
    ///         <item>Only mods listed in <c>definition.Requires</c></item>
    ///     </list>
    /// </summary>
    private static DefaultAssemblyResolver CreateAssemblyResolver(IDirectoryStatic directory, ModDefinition definition, string assemblyPath) {
        var resolver = new DefaultAssemblyResolver();
        resolver.RemoveSearchDirectory(".");
        resolver.RemoveSearchDirectory("bin");

        resolver.AddSearchDirectory(Path.Combine(directory.GetCurrentDirectory(), "Railroader_Data", "Managed"));

        //  stryker disable once block
        if (definition.Requires.Count == 0) {
            return resolver;
        }

        var thisModDir = Path.GetDirectoryName(assemblyPath);
        var modDirs = directory.EnumerateDirectories(Path.Combine(directory.GetCurrentDirectory(), "Mods"))
                      .Where(o => o != thisModDir)
                      .Where(o => definition.Requires.ContainsKey(Path.GetFileName(o)));
        foreach (var modDir in modDirs) {
            resolver.AddSearchDirectory(modDir);
        }

        return resolver;
    }

    /// <summary>
    ///     Applies all patchers that match implemented marker interfaces on each type.
    /// </summary>
    /// <returns>
    ///     <c>(shouldReplaceOriginal, success)</c> – whether a patched file should replace the original,
    ///     and whether the operation succeeded without errors.
    /// </returns>
    private static (bool shouldReplaceOriginal, bool success) TryPatchAssemblyDefinition(ILogger logger, ModDefinition definition, TypePatcherInfo[] pluginPatchers, IAssemblyDefinition assemblyDefinition) {
        var hasPatch = false;
        var hasError = false;
        foreach (var type in assemblyDefinition.MainModule.Types) {
            try {
                var interfaces = type.Interfaces.Select(i => i.InterfaceType!.FullName).ToList();
                var patchers   = pluginPatchers.Where(pair => interfaces.Contains(pair.MarkerType.FullName)).Select(o => o.Factory());

                foreach (var patcher in patchers) {
                    hasPatch = true;
                    patcher!(assemblyDefinition, type);
                }
            } catch (Exception ex) {
                logger.Error(ex, "Failed to patch type {TypeName} for mod {ModId}", type.FullName, definition.Identifier);
                hasError = true;
            }
        }

        return (hasPatch && !hasError, !hasError);
    }
}

using System.Collections.Concurrent;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using JetBrains.Annotations;
using RailManager.Extensions;
using RailManager.Interfaces;
using RailManager.Interfaces.Markers;
using RailManager.Wrappers.HarmonyLib;
using Serilog;

namespace RailManager.Features.CodePatchers;

/// <summary>
///     Patches types implementing <see cref="IHarmonyPlugin" /> to apply or remove Harmony patches when
///     <c>OnIsEnabledChanged</c> is called.
/// </summary>
[PublicAPI]
public sealed class HarmonyPluginPatcher
{
    private static readonly ConcurrentDictionary<IPlugin, PatcherState> _States = new();

    [ExcludeFromCodeCoverage]
    public static TypePatcherDelegate Factory() => Factory(Log.Logger.ForSourceContext());

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static TypePatcherDelegate Factory(ILogger logger) {
        var method = MethodPatcher.Factory<IHarmonyPlugin>(logger, typeof(HarmonyPluginPatcher), typeof(PluginBase<>),
                                                           "OnIsEnabledChanged");
        return (assemblyDefinition, typeDefinition) => method(assemblyDefinition, typeDefinition);
    }

    /// <summary>
    ///     Handles the <c>OnIsEnabledChanged</c> event for the plugin, performing patcher-specific logic when the plugin
    ///     is enabled or disabled.
    /// </summary>
    /// <param name="plugin">The plugin instance. Must not be null.</param>
    /// <remarks>Method called from plugin.</remarks>
    [UsedImplicitly]
    public static void OnIsEnabledChanged(IHarmonyPlugin plugin) {
        var context = (ModdingContext)plugin.ModdingContext;

        var state = _States.GetOrAdd(plugin,
                                     _ => new(!plugin.IsEnabled, context.HarmonyFactory(plugin.Mod.Definition.Identifier)))!;

        if (state.IsEnabled == plugin.IsEnabled) {
            return;
        }

        _States[plugin] = state with { IsEnabled = plugin.IsEnabled };

        if (plugin.IsEnabled) {
            context.Logger.Information("Applying Harmony patch for mod {ModId}", plugin.Mod.Definition.Identifier);
            state.Harmony.PatchAll(plugin.GetType().Assembly);
        } else {
            context.Logger.Information("Removing Harmony patch for mod {ModId}", plugin.Mod.Definition.Identifier);
            state.Harmony.UnpatchAll(plugin.Mod.Definition.Identifier);
        }
    }

    private sealed record PatcherState(bool IsEnabled, IHarmony Harmony);
}

using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using JetBrains.Annotations;
using Mono.Cecil;
using Mono.Cecil.Cil;
using RailManager.Exceptions;
using RailManager.Extensions;
using RailManager.Wrappers.Mono.Cecil;
using Serilog;
using MethodAttributes = Mono.Cecil.MethodAttributes;

namespace RailManager.Features.CodePatchers;

public delegate bool MethodPatcherDelegate(IAssemblyDefinition assemblyDefinition, TypeDefinition typeDefinition);

[PublicAPI]
public static class MethodPatcher
{
    [ExcludeFromCodeCoverage]
    public static MethodPatcherDelegate Factory<TMarker>(
        Type patcherType,
        Type targetBaseType,
        string targetMethod,
        string? injectorMethod = null
    ) =>
        Factory<TMarker>(Log.Logger.ForSourceContext(), patcherType, targetBaseType, targetMethod, injectorMethod);

    [EditorBrowsable(EditorBrowsableState.Never)]
    public static MethodPatcherDelegate Factory<TMarker>(
        ILogger logger,
        Type patcherType,
        Type targetBaseType,
        string targetMethod,
        string? injectorMethod = null
    ) {
        var injected = patcherType.GetMethod(injectorMethod ?? targetMethod, BindingFlags.Public | BindingFlags.Static);

        var errors = ValidateInjector<TMarker>(injected).ToList();
        if (injected == null || errors.Count > 0) {
            throw new ValidationException("Failed to resolve injected method. See errors for details.", errors);
        }

        var ctx = new PatcherContext(logger, typeof(TMarker), targetBaseType, targetMethod, injected);
        return (asm, type) => ctx.Execute(asm, type);
    }

    private static IEnumerable<string> ValidateInjector<TMarker>(MethodInfo? method) {
        if (method == null) {
            yield return "Injected method must be public and static.";
            yield break;
        }

        if (method.ReturnType != typeof(void)) {
            yield return "Injected method must bave void return type.";
        }

        if (!method.DeclaringType!.IsPublic) {
            yield return "Injected method declaring type must be public.";
        }

        var pars = method.GetParameters();
        if (pars.Length != 1 || !pars[0].ParameterType.IsAssignableFrom(typeof(TMarker))) {
            yield return $"Injected method must have single parameter assignable from {typeof(TMarker)}.";
        }
    }

    private static bool Execute(this PatcherContext ctx, IAssemblyDefinition asm, TypeDefinition type) {
        if (!ctx.IsApplicable(type)) {
            return false;
        }

        var module = asm.MainModule;
        var method = type.Methods.FirstOrDefault(m => m.Name == ctx.TargetMethod) ?? ctx.CreateOverride(type, module);

        if (method == null) {
            return false;
        }

        var injectedRef = module.ImportReference(ctx.InjectedMethod);
        var il          = method.Body.GetILProcessor();

        if (il.HasCallTo(injectedRef)) {
            ctx.Logger.Information("Skipping patch of {TypeName} as it already contain code for {PluginInterface}",
                                   type.FullName, ctx.MarkerType);
            return false;
        }

        // inject at the very start
        var first = method.Body.Instructions[0]!;
        il.InsertBefore(first, il.Create(OpCodes.Ldarg_0));
        il.InsertBefore(first, il.Create(OpCodes.Call, injectedRef));

        ctx.Logger.Information("Successfully patched {TypeName} for {PluginInterface}", type.FullName, ctx.MarkerType);

        return true;
    }

    private static bool IsApplicable(this PatcherContext ctx, TypeDefinition type) {
        var derived = ctx.IsDerivedFromBase(type);
        var marker  = type.Interfaces.Any(i => i.InterfaceType?.FullName == ctx.MarkerType.FullName);

        if (!derived || !marker) {
            ctx.Logger.Debug(
                "Skipping patching for type {TypeName}: not derived from {BaseType} or does not implement {MarkerInterface}",
                type.FullName, ctx.TargetBaseType, ctx.MarkerType);
            return false;
        }

        return true;
    }

    private static bool IsDerivedFromBase(this PatcherContext ctx, TypeDefinition type) {
        Func<TypeReference?, string?> getName = ctx.TargetBaseType.IsGenericTypeDefinition
            ? r => (r as GenericInstanceType)?.ElementType?.FullName
            : r => r?.FullName;

        var cur = type;
        while (cur != null) {
            if (getName(cur.BaseType) == ctx.TargetBaseType.FullName) {
                return true;
            }

            cur = cur.BaseType?.Resolve();
        }

        return false;
    }

    private static MethodDefinition? CreateOverride(
        this PatcherContext ctx,
        TypeDefinition type,
        ModuleDefinition module
    ) {
        ctx.Logger.Debug("{MethodName} method not found in {TypeName}, creating override", ctx.TargetMethod,
                         type.FullName);

        var baseMethod = ctx.FindVirtualBaseMethod(type.BaseType);
        if (baseMethod == null) {
            ctx.Logger.Error("Virtual method '{MethodName}' not found in {TypeName} hierarchy!", ctx.TargetMethod,
                             type.FullName);
            return null;
        }

        var baseRef = module.ImportReference(baseMethod);
        var attrs = (baseMethod.Attributes & ~(MethodAttributes.Final | MethodAttributes.NewSlot)) |
                    MethodAttributes.HideBySig;

        var method = new MethodDefinition(ctx.TargetMethod, attrs, module.ImportReference(baseMethod.ReturnType));

        foreach (var p in baseMethod.Parameters) {
            method.Parameters.Add(
                new(p.Name, p.Attributes, module.ImportReference(p.ParameterType)));
        }

        var il = method.Body.GetILProcessor();
        il.Emit(OpCodes.Ldarg_0);

        for (var i = 0; i < baseMethod.Parameters.Count; i++) {
            var ilCode = i switch {
                0 => il.Create(OpCodes.Ldarg_1),
                1 => il.Create(OpCodes.Ldarg_2),
                2 => il.Create(OpCodes.Ldarg_3),
                _ => il.Create(OpCodes.Ldarg_S, method.Parameters[i]!)
            };
            il.Append(ilCode);
        }

        il.Emit(OpCodes.Call, baseRef);
        il.Emit(OpCodes.Ret);

        type.Methods.Add(method);
        ctx.Logger.Debug("Created {MethodName} override with base call in {TypeName}", ctx.TargetMethod, type.FullName);
        return method;
    }

    private static MethodDefinition? FindVirtualBaseMethod(this PatcherContext ctx, TypeReference? baseRef) {
        var cur = baseRef?.Resolve();
        while (cur != null) {
            var m = cur.Methods.FirstOrDefault(m => m.Name == ctx.TargetMethod && m.IsVirtual);
            if (m != null) {
                return m;
            }

            cur = cur.BaseType?.Resolve();
        }

        return null;
    }


    private static bool HasCallTo(this ILProcessor il, MethodReference target) =>
        il.Body!.Instructions.Any(i =>
                                      i.OpCode == OpCodes.Call && i.Operand is MethodReference mr && mr.FullName == target.FullName);

    private sealed record PatcherContext(
        ILogger Logger,
        Type MarkerType,
        Type TargetBaseType,
        string TargetMethod,
        MethodInfo InjectedMethod
    );
}

