using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using Newtonsoft.Json.Linq;
using RailManager.Services;
using RailManager.Wrappers.System.IO;
using Path = System.IO.Path;

namespace RailManager.Features;

/// <summary>
///     Delegate that represents a method for loading all available <see cref="ModDefinition" /> instances.
/// </summary>
/// <returns>
///     An array of loaded <see cref="ModDefinition" /> objects.
///     If no definitions could be loaded, an empty array is returned.
/// </returns>
public delegate ModDefinition[] LoadDefinitionsDelegate();

/// <summary>
///     Provides functionality to locate, read, and parse mod definition files
///     from the <c>Mods</c> directory in the current working directory.
/// </summary>
public static class ModDefinitionLoader
{
    /// <summary>
    ///     Creates a default delegate instance that loads all mod definitions
    ///     using real file system and directory operations.
    /// </summary>
    /// <param name="logger">
    ///     The <see cref="IMemoryLogger" /> instance used for diagnostic output.
    /// </param>
    /// <returns>
    ///     A <see cref="LoadDefinitionsDelegate" /> that can be invoked to load mod definitions.
    /// </returns>
    [ExcludeFromCodeCoverage]
    public static LoadDefinitionsDelegate Create(IMemoryLogger logger) =>
        () => LoadDefinitions(logger, FileSystem.Instance);

    /// <summary>
    ///     Loads all valid <see cref="ModDefinition" /> instances from the <c>Mods</c> directory.
    /// </summary>
    /// <param name="logger"> The <see cref="IMemoryLogger" /> used for warnings, informational messages, and errors. </param>
    /// <param name="fileSystem"></param>
    /// <returns>
    ///     An array of valid, distinct <see cref="ModDefinition" /> objects.
    ///     If the <c>Mods</c> directory is missing or no valid definitions are found, an empty array is returned.
    /// </returns>
    public static ModDefinition[] LoadDefinitions(
        IMemoryLogger logger,
        IFileSystem fileSystem
    ) {
        var modDefinitions = new Dictionary<string, ModDefinition>(StringComparer.OrdinalIgnoreCase);

        var baseDirectory = Path.Combine(fileSystem.Directory.GetCurrentDirectory(), "Mods");
        if (!fileSystem.Directory.Exists(baseDirectory)) {
            logger.Warning("Mods directory not found at {baseDirectory}", baseDirectory);
            return [];
        }

        foreach (var modDir in fileSystem.Directory.EnumerateDirectories(baseDirectory)) {
            var definitionPath = Path.Combine(modDir, "Definition.json");
            if (!fileSystem.File.Exists(definitionPath)) {
                logger.Warning("Not loading directory {directory}: Missing Definition.json.", modDir);
                continue;
            }

            logger.Information("Loading definition from {directory} ...", modDir);
            try {
                var jObject = JObject.Parse(fileSystem.File.ReadAllText(definitionPath));
                var modDef  = jObject.ToObject<ModDefinition>()!;
                if (!modDef.IsValid) {
                    logger.Error("Skipping mod at {definitionPath}: Invalid mod definition.", definitionPath);
                    continue;
                }

                if (modDefinitions.TryGetValue(modDef.Identifier, out var existing)) {
                    logger.Error("Duplicate mod identifier '{identifier}' found in '{newDirectory}'. Already defined in '{existingDirectory}'.",
                                 modDef.Identifier, modDir, existing.BasePath);
                    continue;
                }

                modDef.BasePath = modDir;
                modDefinitions.Add(modDef.Identifier, modDef);
            } catch (Exception exc) {
                logger.Error("Failed to parse definition JSON: {exception}", exc);
            }
        }

        return modDefinitions.Values.ToArray();
    }
}

using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using RailManager.Extensions;
using RailManager.Interfaces.Data;
using Serilog;

namespace RailManager.Features;

/// <summary>
///     Delegate that validates and topologically sorts a collection of mod definitions.
/// </summary>
/// <param name="modDefinitions">The mod definitions to validate and sort.</param>
/// <returns>
///     A sorted list of valid mods, or an empty array if validation fails or a cycle is detected.
/// </returns>
public delegate IReadOnlyList<ModDefinition> ValidateMods(IReadOnlyList<ModDefinition> modDefinitions);

/// <summary>
///     Validates mod dependencies, conflicts, and sorts them in load order.
/// </summary>
public static class ModDefinitionValidator
{
    /// <summary>
    ///     Creates a <see cref="ValidateMods" /> delegate that uses the global Serilog logger.
    /// </summary>
    /// <returns>
    ///     A delegate that validates and sorts mods using <see cref="Log.Logger" />.
    /// </returns>
    [ExcludeFromCodeCoverage]
    public static ValidateMods Create => definitions => ValidateAndSort(Log.Logger.ForSourceContext(), definitions);

    /// <summary>
    ///     Validates dependencies and conflicts, then returns a topologically sorted list of mods.
    /// </summary>
    /// <param name="logger">The logger for reporting validation errors and cycles.</param>
    /// <param name="modDefinitions">The collection of mod definitions to process.</param>
    /// <returns>
    ///     A sorted <see cref="IReadOnlyList{ModDefinition}" /> if all checks pass;
    ///     otherwise, an empty array.
    /// </returns>
    public static IReadOnlyList<ModDefinition> ValidateAndSort(ILogger logger, IReadOnlyList<ModDefinition> modDefinitions) =>
        ValidateDependencies(logger, modDefinitions)
            ? SortByDependencies(logger, modDefinitions)
            : [];

    /// <summary>
    ///     Validates that all required mods exist and version constraints are satisfied,
    ///     and that no conflicting mods are present.
    /// </summary>
    /// <param name="logger">The logger for error reporting.</param>
    /// <param name="modDefinitions">The mods to validate.</param>
    /// <returns><c>true</c> if all checks pass; otherwise, <c>false</c>.</returns>
    private static bool ValidateDependencies(ILogger logger, IReadOnlyList<ModDefinition> modDefinitions) {
        var modMap = modDefinitions.ToDictionary(mod => mod.Identifier, mod => mod, StringComparer.OrdinalIgnoreCase);

        var hasError = false;
        foreach (var mod in modDefinitions) {
            // Verify Requirements
            foreach (var (requiredId, fluentVersion) in mod.Requires) {
                hasError = CheckRequirement(logger, mod, modMap, requiredId, fluentVersion) || hasError;
            }

            // Verify Conflicts
            foreach (var (conflictId, fluentVersion) in mod.ConflictsWith) {
                hasError = CheckConflict(logger, mod, modMap, conflictId, fluentVersion) || hasError;
            }
        }

        return !hasError;
    }

    /// <summary>
    ///     Checks a single required mod dependency.
    /// </summary>
    /// <param name="logger">The logger.</param>
    /// <param name="mod">The mod declaring the requirement.</param>
    /// <param name="modMap">Map of all mods by identifier.</param>
    /// <param name="requiredId">The required mod identifier.</param>
    /// <param name="constraint">Optional version constraint.</param>
    /// <returns><c>true</c> if the requirement fails; otherwise, <c>false</c>.</returns>
    private static bool CheckRequirement(ILogger logger, ModDefinition mod, Dictionary<string, ModDefinition> modMap, string requiredId, FluentVersion? constraint) {
        if (!modMap.TryGetValue(requiredId, out var requiredMod)) {
            logger.Error("Mod '{identifier}' requires mod '{requiredId}', but it is not present.", mod.Identifier, requiredId);
            return true;
        }

        if (constraint == null || Satisfies(requiredMod.Version, constraint)) {
            return false;
        }

        logger.Error(
            "Mod '{identifier}' requires mod '{requiredId}' with version constraint '{fluentVersion}', but found version '{version}'.",
            mod.Identifier, requiredId, constraint, requiredMod.Version
        );
        return true;
    }

    /// <summary>
    ///     Checks a single mod conflict.
    /// </summary>
    /// <param name="logger">The logger.</param>
    /// <param name="mod">The mod declaring the conflict.</param>
    /// <param name="modMap">Map of all mods by identifier.</param>
    /// <param name="conflictId">The conflicting mod identifier.</param>
    /// <param name="constraint">Optional version constraint.</param>
    /// <returns><c>true</c> if a conflict exists; otherwise, <c>false</c>.</returns>
    private static bool CheckConflict(ILogger logger, ModDefinition mod, Dictionary<string, ModDefinition> modMap, string conflictId, FluentVersion? constraint) {
        if (!modMap.TryGetValue(conflictId, out var conflictingMod)) {
            return false;
        }

        if (constraint != null && !Satisfies(conflictingMod.Version, constraint)) {
            return false;
        }

        if (constraint != null) {
            logger.Error(
                "Mod '{identifier}' conflicts with mod '{conflictId}' (version: '{version}', constraint: '{fluentVersion}').",
                mod.Identifier, conflictId, conflictingMod.Version, constraint
            );
        } else {
            logger.Error(
                "Mod '{identifier}' conflicts with mod '{conflictId}' (version: '{version}').",
                mod.Identifier, conflictId, conflictingMod.Version
            );
        }

        return true;
    }

    /// <summary>
    ///     Performs a topological sort of mods based on <c>Requires</c> dependencies.
    ///     Detects and reports cyclic dependencies.
    /// </summary>
    /// <param name="logger">The logger for cycle detection.</param>
    /// <param name="modDefinitions">The mods to sort.</param>
    /// <returns>
    ///     A sorted list if no cycles exist; otherwise, an empty array (load fails entirely).
    /// </returns>
    /// <remarks>
    ///     Uses depth-first search (DFS) with path tracking to detect cycles.
    ///     <para>
    ///         If a cyclic dependency is detected <strong>anywhere</strong> in the graph,
    ///         <strong>no mods are returned</strong> — the entire load fails.
    ///         This ensures no partial or broken load order.
    ///     </para>
    /// </remarks>
    private static IReadOnlyList<ModDefinition> SortByDependencies(ILogger logger, IReadOnlyList<ModDefinition> modDefinitions) {
        var modMap = modDefinitions.ToDictionary(mod => mod.Identifier, mod => mod, StringComparer.OrdinalIgnoreCase);

        var sorted      = new List<ModDefinition>();
        var visited     = new HashSet<string>(StringComparer.OrdinalIgnoreCase);
        var invalidMods = new HashSet<string>(StringComparer.OrdinalIgnoreCase);

        var hasCycle = false;
        foreach (var mod in modDefinitions) {
            if (!visited.Contains(mod.Identifier)) {
                hasCycle = !Visit(mod, new()) || hasCycle;
            }
        }

        return hasCycle ? [] : sorted.ToArray();

        bool Visit(ModDefinition mod, Stack<string> path) {
            if (path.Contains(mod.Identifier)) {
                path.Push(mod.Identifier);
                logger.Error("Cyclic dependency detected: {dependencyLoop}", string.Join(" -> ", path.Reverse()));
                return false;
            }

            if (!visited.Add(mod.Identifier)) {
                return !invalidMods.Contains(mod.Identifier);
            }

            path.Push(mod.Identifier);

            var isValid = true;
            foreach (var requiredId in mod.Requires.Keys) {
                if (invalidMods.Contains(requiredId)) {
                    logger.Error(
                        "Mod '{identifier}' cannot resolve mod '{requiredId}' because mod is part of a cyclic dependency.",
                        mod.Identifier, requiredId
                    );
                } else if (!Visit(modMap[requiredId]!, path)) {
                    isValid = false;
                }
            }


            path.Pop();

            if (isValid) {
                sorted.Add(mod);
            } else {
                invalidMods.Add(mod.Identifier);
            }

            return isValid;
        }
    }

    /// <summary>
    ///     Determines whether a version satisfies a fluent version constraint.
    /// </summary>
    /// <param name="actualVersion">The actual mod version.</param>
    /// <param name="constraint">The version constraint to satisfy.</param>
    /// <returns><c>true</c> if the version meets the constraint; otherwise, <c>false</c>.</returns>
    /// <exception cref="InvalidOperationException">
    ///     Thrown when an unknown <see cref="VersionOperator" /> is encountered.
    /// </exception>
    private static bool Satisfies(Version actualVersion, FluentVersion constraint) {
        return constraint.Operator switch {
            VersionOperator.Equal          => actualVersion == constraint.Version,
            VersionOperator.GreaterThan    => actualVersion > constraint.Version,
            VersionOperator.GreaterOrEqual => actualVersion >= constraint.Version,
            VersionOperator.LessThan       => actualVersion < constraint.Version,
            VersionOperator.LessOrEqual    => actualVersion <= constraint.Version,
            _                              => throw new InvalidOperationException($"Unknown version operator: {constraint.Operator}")
        };
    }
}

using System;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.IO;
using JetBrains.Annotations;
using Newtonsoft.Json;
using RailManager.Services;
using RailManager.Wrappers.System.IO;

namespace RailManager.Features;

/// <summary>
///     Represents an action that extracts all mod archives from the <c>Mods</c> directory.
/// </summary>
public delegate void ModExtractionAction();

/// <summary>
///     Provides functionality to extract mod archives (.zip) containing a valid <c>Definition.json</c>.
/// </summary>
[PublicAPI]
public static class ModExtractor
{
    /// <summary>
    ///     Creates a <see cref="ModExtractionAction" /> that extracts all mod archives using the specified logger.
    /// </summary>
    /// <param name="logger">The logger used to record extraction progress and errors.</param>
    /// <returns>A delegate that, when invoked, performs the full extraction process.</returns>
    [ExcludeFromCodeCoverage]
    public static ModExtractionAction GetExtractor(IMemoryLogger logger) =>
        () => ExtractAll(logger, FileSystem.Instance);

    /// <summary>
    ///     Extracts all <c>*.zip</c> files from the <c>Mods</c> directory that contain a valid <c>Definition.json</c>.
    /// </summary>
    /// <param name="logger">The logger for reporting progress and errors.</param>
    /// <param name="fileSystem"></param>
    /// <remarks>
    ///     Each archive is processed independently. Invalid or duplicate mods are skipped with appropriate logging.
    ///     Successfully extracted archives are moved to a <c>.bak</c> backup with a unique name if needed.
    /// </remarks>
    [EditorBrowsable(EditorBrowsableState.Never)]
    public static void ExtractAll(IMemoryLogger logger, IFileSystem fileSystem) {
        var modsDirectory = Path.Combine(fileSystem.Directory.GetCurrentDirectory(), "Mods");
        var zipFiles      = fileSystem.DirectoryInfo(modsDirectory).EnumerateFiles("*.zip");

        foreach (var zipFileInfo in zipFiles) {
            try {
                TryExtractOne(logger, fileSystem, zipFileInfo, modsDirectory);
            } catch (Exception exc) {
                logger.Error(exc, "Failed to unzip archive {ZipPath}.", zipFileInfo.FullName);
            }
        }
    }

    /// <summary>
    ///     Attempts to extract a single mod archive.
    /// </summary>
    /// <param name="logger">The logger instance.</param>
    /// <param name="fileSystem"></param>
    /// <param name="zipFileInfo">The ZIP file to process.</param>
    /// <param name="modsDirectory">The root directory containing mods.</param>
    /// <remarks>
    ///     Early returns occur if:
    ///     <list type="bullet">
    ///         <item>
    ///             <description><c>Definition.json</c> is missing</description>
    ///         </item>
    ///         <item>
    ///             <description>JSON parsing fails</description>
    ///         </item>
    ///         <item>
    ///             <description><see cref="ModDefinition.IsValid" /> is <c>false</c></description>
    ///         </item>
    ///         <item>
    ///             <description>Target extraction directory already exists</description>
    ///         </item>
    ///     </list>
    ///     On success, the archive is moved to a <c>.bak</c> backup.
    /// </remarks>
    private static void TryExtractOne(IMemoryLogger logger, IFileSystem fileSystem, IFileInfo zipFileInfo, string modsDirectory) {
        logger.Information("Processing mod archive {ZipPath} for extraction.", zipFileInfo.FullName);

        using (var archive = fileSystem.ZipFile.OpenRead(zipFileInfo.FullName)) {
            var definitionEntry = archive.GetEntry("Definition.json");
            if (definitionEntry == null) {
                logger.Error("Skipping archive {ZipPath}: Missing 'Definition.json'.", zipFileInfo.FullName);
                return;
            }

            string json;
            using (var entryStream = definitionEntry.Open())
            using (var reader = new StreamReader(entryStream)) {
                json = reader.ReadToEnd();
            }

            var modDefinition = TryDeserialize(logger, json, zipFileInfo.FullName);
            if (modDefinition?.IsValid != true) {
                logger.Error("Skipping archive {ZipPath}: Invalid mod definition.", zipFileInfo.FullName);
                return;
            }

            var extractPath = Path.Combine(modsDirectory, modDefinition.Identifier);

            if (fileSystem.Directory.Exists(extractPath)) {
                logger.Warning("Extraction path {ExtractPath} already exists – skipping mod {ModId}.", extractPath, modDefinition.Identifier);
                MoveToBackup(zipFileInfo, logger, ".dup", fileSystem.File);
                return;
            }

            fileSystem.ZipFile.ExtractToDirectory(zipFileInfo.FullName, extractPath);

            logger.Information("Successfully extracted mod {ModId} from {ZipPath} to {ExtractPath}.", modDefinition.Identifier, zipFileInfo.FullName,
                extractPath);
        }

        MoveToBackup(zipFileInfo, logger, ".bak", fileSystem.File);
    }

    /// <summary>
    ///     Attempts to deserialize the <c>Definition.json</c> content into a <see cref="ModDefinition" />.
    /// </summary>
    /// <param name="logger">The logger for reporting JSON parsing errors.</param>
    /// <param name="json">The JSON string from <c>Definition.json</c>.</param>
    /// <param name="zipPath">The path of the ZIP file, used for logging context.</param>
    /// <returns>The deserialized <see cref="ModDefinition" />, or <c>null</c> if parsing fails.</returns>
    private static ModDefinition? TryDeserialize(IMemoryLogger logger, string json, string zipPath) {
        try {
            return JsonConvert.DeserializeObject<ModDefinition>(json);
        } catch (JsonException ex) {
            logger.Error(ex, "Skipping archive {ZipPath}: Failed to parse Definition.json.", zipPath);
            return null;
        }
    }

    /// <summary>
    ///     Moves the processed ZIP file to a backup location with the specified extension.
    /// </summary>
    /// <param name="zipFile">The ZIP file to back up.</param>
    /// <param name="logger">The logger for reporting backup failures.</param>
    /// <param name="extension">
    ///     The backup file extension (e.g., <c>".bak"</c> or <c>".dup"</c>).
    /// </param>
    /// <param name="file"></param>
    /// <remarks>
    ///     If a file with the target name already exists, a numeric suffix is appended
    ///     (e.g., <c>mod.zip.bak</c> → <c>mod.zip.bak1</c>) until a unique name is found.
    /// </remarks>
    private static void MoveToBackup(IFileInfo zipFile, IMemoryLogger logger, string extension, IFileStatic file) {
        var basePath   = Path.ChangeExtension(zipFile.FullName, extension);
        var backupPath = basePath;

        var i = 1;
        while (file.Exists(backupPath)) {
            backupPath = $"{basePath}{i++}";
        }

        zipFile.MoveTo(backupPath);
    }
}

using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using RailManager.Extensions;
using RailManager.Interfaces;
using RailManager.Wrappers.System.Reflection.Assembly;
using Serilog;

namespace RailManager.Features;

/// <summary>
///     Creates a reusable plugin loader for a modding context.
/// </summary>
internal delegate PluginLoader PluginLoaderFactory(IModdingContext moddingContext);

/// <summary>
///     Loads all plugins from a <see cref="Mod" /> using the pre-configured context and logger.
/// </summary>
/// <param name="mod">The mod to load plugins from.</param>
/// <returns>An array of instantiated <see cref="IPlugin" /> instances.</returns>
internal delegate IPlugin[] PluginLoader(Mod mod);

/// <summary>
///     Manages discovery and instantiation of mod plugins via reflection.
/// </summary>
internal static class PluginManager
{
    /// <summary>
    ///     Creates a high-performance, reusable plugin loader for the given modding context.
    /// </summary>
    /// <param name="moddingContext">The modding context to inject into plugin constructors.</param>
    /// <returns>
    ///     A <see cref="PluginLoader" /> delegate that can be reused to load plugins from multiple mods
    ///     without reallocating logger or context.
    /// </returns>
    [ExcludeFromCodeCoverage]
    public static PluginLoader CreateLoader(IModdingContext moddingContext) =>
        mod => LoadPlugins(moddingContext, Log.Logger.ForSourceContext(), Assembly.LoadFrom, mod);

    /// <summary>
    ///     Loads and instantiates all valid plugins from the given mod.
    /// </summary>
    /// <param name="moddingContext">The modding context to pass to plugin constructors.</param>
    /// <param name="logger">The logger instance (typically scoped to the mod).</param>
    /// <param name="loadFrom">Delegate used to load the assembly from path (e.g. <c>Assembly.LoadFrom</c>).</param>
    /// <param name="mod">The mod containing the plugin assembly.</param>
    /// <returns>An array of instantiated plugins implementing <see cref="IPlugin" />.</returns>
    /// <exception cref="InvalidOperationException">
    ///     Thrown if <c>mod.AssemblyPath</c> is <c>null</c> — violates mod contract.
    /// </exception>
    [EditorBrowsable(EditorBrowsableState.Never)]
    public static IPlugin[] LoadPlugins(IModdingContext moddingContext, ILogger logger, LoadFrom loadFrom, Mod mod) =>
        InstantiatePlugins(moddingContext, logger, loadFrom, mod).ToArray();

    /// <summary>
    ///     Scans the mod assembly and instantiates all valid plugin types.
    /// </summary>
    /// <param name="moddingContext">Context injected into plugin constructors.</param>
    /// <param name="logger">Logger for warnings (e.g. missing ctor, wrong base class).</param>
    /// <param name="loadFrom">Assembly loading strategy.</param>
    /// <param name="mod">Source mod.</param>
    /// <returns>
    ///     A sequence of instantiated <see cref="IPlugin" /> objects. Empty if assembly fails to load.
    /// </returns>
    private static IEnumerable<IPlugin> InstantiatePlugins(IModdingContext moddingContext, ILogger logger, LoadFrom loadFrom, Mod mod) {
        var assemblyPath = mod.AssemblyPath ?? throw new InvalidOperationException("Mod contract violation: AssemblyPath is null.");

        var assembly = loadFrom(assemblyPath);
        if (assembly == null) {
            logger.Warning("Failed to load assembly from path: {AssemblyPath} for mod {ModId}",
                           assemblyPath, mod.Definition.Identifier);
            yield break;
        }

        var pluginBaseType = typeof(PluginBase<>);
        var types          = assembly.GetTypes();
        foreach (var type in types) {
            if (type.IsAbstract) {
                continue;
            }

            if (type.BaseType is not { IsGenericType: true } baseType ||
                baseType.GetGenericTypeDefinition() != pluginBaseType) {
                if (typeof(IPlugin).IsAssignableFrom(type)) {
                    logger.Warning(
                        "Type {Type} implements IPlugin but does not inherit from PluginBase<> in mod {ModId}",
                        type.FullName, mod.Definition.Identifier);
                }

                continue;
            }

            var ctor = type.GetConstructor(BindingFlags.Public | BindingFlags.NonPublic | BindingFlags.Instance, null!, [typeof(IModdingContext), typeof(IMod)], null!);
            if (ctor == null) {
                logger.Warning(
                    "Cannot find constructor (IModdingContext, IMod) on plugin {Plugin} in mod {ModId}",
                    type.FullName, mod.Definition.Identifier);
                continue;
            }

            yield return (IPlugin)ctor.Invoke([moddingContext, mod])!;
        }
    }
}

using System;
using System.Collections;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using HarmonyLib;
using JetBrains.Annotations;
using Logging;
using RailManager.Features;
using RailManager.Services;
using Serilog;
using Serilog.Events;

namespace RailManager.HarmonyPatches;

[UsedImplicitly(ImplicitUseTargetFlags.Members)]
[HarmonyPatch]
[HarmonyPatchCategory("LogManager")]
[ExcludeFromCodeCoverage]
public static class LogManagerPatch
{
    [HarmonyPrefix]
    [HarmonyPatch(typeof(LogManager), "Awake")]
    public static void AwakePrefix(out MemoryLogger __state) {
        __state = (MemoryLogger)Log.Logger!;
    }

    [HarmonyPostfix]
    [HarmonyPatch(typeof(LogManager), "Awake")]
    public static void AwakePostfix(ref MemoryLogger __state) {
        __state.Information("Finalizing logger configuration");
        var logger = Log.Logger!;
        __state.Flush(logger);
        logger.Information("Logger configured successfully");
    }

    [HarmonyPostfix]
    [HarmonyPatch(typeof(LogManager), "MakeConfiguration")]
    public static void MakeConfigurationPostfix(ref LoggerConfiguration __result) {
        var logger = Log.Logger!;
        try {
            foreach (var pair in Bootstrapper.ModDefinitions.Where(o =>
                         o.LogLevel != null && o.LogLevel != LogEventLevel.Information)) {
                var identifier = pair.Identifier;
                if (identifier == "") {
                    identifier = "Railroader.ModManager";
                }

                logger.Information("Setting log level for {identifier} to {level}", identifier, pair.LogLevel!.Value);
                __result.MinimumLevel.Override(identifier, pair.LogLevel.Value);
            }

            RemoveUnitySinks(__result);

            // Configure modded sinks
            __result.WriteTo.Conditional(o => o.Properties.ContainsKey("SourceContext"),
                o => o.UnityConsole(
                    "[{Timestamp:HH:mm:ss} {Level:u3}] [{SourceContext}] {Message:lj}{NewLine}{Exception}"));
            __result.WriteTo.Conditional(o => !o.Properties.ContainsKey("SourceContext"),
                o => o.UnityConsole("[{Timestamp:HH:mm:ss} {Level:u3}] {Message:lj}{NewLine}{Exception}"));
        } catch (Exception exc) {
            logger.Error(exc, "Failed to configure serilog");
        }
    }

    /// <summary> Removes vanilla Unity console sinks from the configuration. </summary>
    /// <param name="configuration">The logger configuration to modify.</param>
    private static void RemoveUnitySinks(LoggerConfiguration configuration) {
        var field = typeof(LoggerConfiguration).GetField("_logEventSinks",
            BindingFlags.NonPublic | BindingFlags.Instance);
        if (field == null) {
            throw new InvalidOperationException($"Unable to get field {typeof(LoggerConfiguration)}::_logEventSinks");
        }

        var sinks = (IList)field.GetValue(configuration)!;

        foreach (var sink in sinks.OfType<SerilogUnityConsoleEventSink>().ToList()) {
            sinks.Remove(sink);
        }
    }
}

using System;
using System.Collections.Generic;
using System.Diagnostics.CodeAnalysis;
using System.Linq;
using System.Reflection;
using HarmonyLib;
using JetBrains.Annotations;
using RailManager.Interfaces.UI;
using UI;

namespace RailManager.HarmonyPatches;

[PublicAPI]
[HarmonyPatch]
public static class ProgrammaticWindowCreatorPatches
{
    private static Dictionary<Type, object> _RegisteredWindows = new();
    private static bool                     _Started;

    [HarmonyPostfix]
    [HarmonyPatch(typeof(ProgrammaticWindowCreator), "Start")]
    [ExcludeFromCodeCoverage]
    public static void Start(ProgrammaticWindowCreator __instance) {
        _Started = true;

        var methodInfo =
            typeof(ProgrammaticWindowCreator)
                .GetMethods(BindingFlags.Instance | BindingFlags.NonPublic)
                .FirstOrDefault(o => o.IsGenericMethod && o.Name == "CreateWindow" && o.GetParameters().Length == 1);

        if (methodInfo == null) {
            throw new InvalidOperationException("Cannot find method UI.ProgrammaticWindowCreator:CreateWindow<TWindow>(Action<>).");
        }

        foreach (var pair in _RegisteredWindows) {
            methodInfo.MakeGenericMethod(pair.Key).Invoke(__instance, [pair.Value]);
        }
    }

    public static void RegisterWindow<TWindow>() where TWindow : ProgrammaticWindowBase {
        if (_Started) {
            throw new InvalidOperationException("Cannot register window: Game already started.");
        }

        var type = typeof(TWindow);
        _RegisteredWindows[type] = new Action<TWindow>(window => _RegisteredWindows[type] = window);
    }

    public static TWindow GetWindow<TWindow>() where TWindow : ProgrammaticWindowBase {
        if (!_Started) {
            throw new InvalidOperationException("Cannot get window: Game not started.");
        }

        var type = typeof(TWindow);
        _RegisteredWindows.TryGetValue(type, out var instance);
        return instance as TWindow ?? throw new InvalidOperationException($"Cannot find window {type}.");
    }
}

using System;
using System.Linq;
using Newtonsoft.Json;
using Serilog.Events;

namespace RailManager.JsonConverters;

/// <summary> JsonConverter for <see cref="LogEventLevel"/>. </summary>
public sealed class LogEventLevelJsonConverter : JsonConverter<LogEventLevel?>
{
    /// <inheritdoc />
    public override void WriteJson(JsonWriter writer, LogEventLevel? value, JsonSerializer serializer) {
        writer.WriteValue(value.ToString());
    }

    /// <inheritdoc />
    public override LogEventLevel? ReadJson(
        JsonReader reader,
        Type objectType,
        LogEventLevel? existingValue,
        bool hasExistingValue,
        JsonSerializer serializer
    ) {
        switch (reader.TokenType) {
            case JsonToken.Null:
                return null;

            case JsonToken.String: {
                var raw = (string)reader.Value!;
                if (!Enum.TryParse<LogEventLevel>(raw, true, out var parsed)) {
                    var validValues = Enum.GetValues(typeof(LogEventLevel)).Cast<LogEventLevel>().Select(o => $"'{o}'");
                    throw new JsonReaderException(
                        $"Unexpected token value '{raw}' when reading {typeof(LogEventLevel?)}. Expected: {string.Join(", ", validValues)} or null");
                }

                return parsed;
            }

            default:
                throw new JsonReaderException(
                    $"Unexpected token type {reader.TokenType} when reading {typeof(LogEventLevel?)}. Expected: {JsonToken.Null} or {JsonToken.String}.");
        }
    }
}

using System;
using System.Collections.Generic;
using System.Linq;
using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using RailManager.Interfaces.Data;

namespace RailManager.JsonConverters;

/// <summary> JsonConverter for <see cref="Dictionary{String,FluentVersion}"/>. </summary>
public sealed class ModReferenceJsonConverter : JsonConverter<Dictionary<string, FluentVersion?>>
{
    /// <inheritdoc />
    public override void WriteJson(
        JsonWriter writer,
        Dictionary<string, FluentVersion?>? value,
        JsonSerializer serializer
    ) {
        writer.WriteStartObject();
        foreach (var pair in value!.OrderBy(o => o.Key)) {
            writer.WritePropertyName(pair.Key);
            if (pair.Value == null) {
                writer.WriteNull();
            } else {
                writer.WriteValue(pair.Value.ToString());
            }
        }
        writer.WriteEndObject();
    }

    /// <inheritdoc />
    public override Dictionary<string, FluentVersion?> ReadJson(
        JsonReader reader,
        Type objectType,
        Dictionary<string, FluentVersion?>? existingValue,
        bool hasExistingValue,
        JsonSerializer serializer
    ) {
        if (reader.TokenType == JsonToken.Null) {
            return new();
        }

        var jObject = JObject.Load(reader);
        var result  = new Dictionary<string, FluentVersion?>();

        foreach (var property in jObject.Properties()) {
            var identifier = property.Name;
            if (property.Value.Type == JTokenType.Null) {
                result.Add(identifier, null);
                continue;
            }

            if (property.Value.Type != JTokenType.String) {
                throw new JsonSerializationException(
                    $"Invalid version constraint for mod '{identifier}' in {objectType}. Expected a string.");
            }

            var constraint    = property.Value.ToString();
            var fluentVersion = ParseVersionConstraint(constraint, identifier);
            result.Add(identifier, fluentVersion);
        }

        return result;
    }

    private static FluentVersion? ParseVersionConstraint(string constraint, string identifier) {
        if (string.IsNullOrEmpty(constraint)) {
            return null;
        }


        var (op, skip) = constraint[0] switch {
            '>' => constraint.Length > 1 && constraint[1] == '='
                ? (VersionOperator.GreaterOrEqual, 2)
                : (VersionOperator.GreaterThan, 1),
            '<' => constraint.Length > 1 && constraint[1] == '='
                ? (VersionOperator.LessOrEqual, 2)
                : (VersionOperator.LessThan, 1),
            '='                                                                => (VersionOperator.Equal, 1),
            '0' or '1' or '2' or '3' or '4' or '5' or '6' or '7' or '8' or '9' => (VersionOperator.GreaterOrEqual, 0),
            _                                                                  => (VersionOperator.Equal, -1)
        };

        if (skip != -1) {
            var version = VersionJsonConverter.ParseString(constraint.Substring(skip).TrimStart());
            if (version != null) {
                return new FluentVersion(version, op);
            }
        }


        throw new JsonSerializationException(
            $"Invalid version constraint '{constraint}' for mod '{identifier}'. Expected a valid System.Version or an operator (=, >, >=, <, <=) followed by a version.");
    }
}

using System;
using Newtonsoft.Json;

namespace RailManager.JsonConverters;

/// <summary> JsonConverter for <see cref="Version"/>. </summary>
public sealed class VersionJsonConverter : JsonConverter<Version>
{
    private const string Expected = "Expected a valid System.Version (e.g., '1', '1.2', '1.2.3' or '1.2.3.4').";

    /// <inheritdoc />
    public override void WriteJson(JsonWriter writer, Version? value, JsonSerializer serializer) {
        writer.WriteValue(value!.ToString());
    }

    /// <inheritdoc />
    public override Version ReadJson(
        JsonReader reader,
        Type objectType,
        Version? existingValue,
        bool hasExistingValue,
        JsonSerializer serializer
    ) {
        if (reader.TokenType == JsonToken.String) {
            var value = Convert.ToString(reader.Value!);

            var version = ParseString(value);
            if (version == null) {
                throw new JsonSerializationException($"Invalid version format '{value}'. {Expected}");
            }

            return version;
        }

        throw new JsonSerializationException($"Invalid version token {reader.TokenType}. {Expected}");
    }

    public static Version? ParseString(string value) {
        if (value.IndexOf('.') == -1) { // to support "version": "1" instead of "1.0"
            value += ".0";
        }

        return Version.TryParse(value, out var version) ? version : null;
    }
}

using System.IO;
using System.Linq;
using Newtonsoft.Json;
using Newtonsoft.Json.Serialization;
using RailManager.Extensions;
using RailManager.Interfaces;
using RailManager.Wrappers.System.IO;
using Serilog;

namespace RailManager;

/// <summary> Implementation of <see cref="IMod"/> for a loaded mod instance. </summary>
public sealed class Mod(ILogger logger, ModDefinition modDefinition, IFileStatic file) : IMod
{
    internal static Mod Create(ILogger logger, ModDefinition modDefinition) => 
        new(logger, modDefinition, FileSystem.Instance.File);

    /// <inheritdoc />
    public IModDefinition Definition => modDefinition;

    /// <summary> Gets or sets the output DLL path for this mod. </summary>
    public string? AssemblyPath { get; internal set; }

    private bool _IsEnabled;
    
    /// <inheritdoc />
    public bool IsEnabled {
        get => _IsEnabled;
        internal set {
            if (_IsEnabled == value) {
                return;
            }

            _IsEnabled = value;

            if (Plugins != null) {
                foreach (var plugin in Plugins) {
                    plugin.IsEnabled = value;
                }
            }
        }
    }

    /// <inheritdoc />
    public bool IsValid { get; internal set; }

    /// <inheritdoc />
    public bool IsLoaded { get; internal set; }

    /// <inheritdoc />
    [JsonIgnore]
    public IPlugin[]? Plugins { get; internal set; }

    [JsonProperty("Plugins")]
    public string[]? PluginNames => Plugins?.Select(o => o.GetType().FullName).ToArray();

    /// <inheritdoc />
    public ILogger CreateLogger(string? scope = null) =>
        logger.ForSourceContext(scope == null ? Definition.Identifier : $"{Definition.Identifier}.{scope}");

    private string GetSettingsFilePath(string identifier) => Path.Combine(modDefinition.BasePath, identifier + ".json");
    
    private readonly JsonSerializerSettings _JsonSerializerSettings = new() {
        ContractResolver = new DefaultContractResolver
        {
            NamingStrategy = new CamelCaseNamingStrategy()
        }
    };
    
    /// <inheritdoc />
    public T? LoadSettings<T>(string identifier) where T : class => 
        JsonConvert.DeserializeObject<T>(file.ReadAllText(GetSettingsFilePath(identifier)), _JsonSerializerSettings);

    /// <inheritdoc />
    public void SaveSettings<T>(string identifier, T settings) where T : class => 
        file.WriteAllText(GetSettingsFilePath(identifier), JsonConvert.SerializeObject(settings, _JsonSerializerSettings));
}

using System;
