    public void ConflictWithoutVersion() {
        // Arrange
        var logger = Substitute.For<ILogger>();
        var modDefinitions = new[] {
            CreateModDefinition("A", "1.0.0", conflicts: new() { { "B", null } }),
            CreateModDefinition("B", "1.0.0")
